import numpy as np

"""**Problem 1**"""

# This function approximates the dominant eigenvector of our matrix A.

def evect_approx1(x_0,k):
  A = np.array([[1, 1],
                [2, 0]])
  x_k = x_0
  for _ in range(k):
      x_k = np.dot(A, x_k)
  return x_k

print(evect_approx1(np.array([1, 9]),10))

"""**Problem 2**"""

# This function approximates the dominant eigenvalue of our matrix A.

def eval_approx1(x_0,k):
  A = np.array([[1, 1],
                [2, 0]])
  x_k = x_0
  for _ in range(k):
    x_k = np.dot(A, x_k)
  x_next = np.dot(A, x_k)
  eval = x_next[0] / x_k[0]
  return eval

print(eval_approx1(np.array([1, 9]),10))

"""**Problem 3**"""

# This function approximates the dominant eigenvalue and eigenvector of our matrix A using the normalized iterative process.

def norm_evect_approx1(x_0,k):
  A = np.array([[1, 1],
                [2, 0]])
  x_k = x_0
  for _ in range(k):
      x_k_prev = x_k
      w_k = np.dot(A, x_k)
      x_k = w_k / np.linalg.norm(w_k)

  eigenvalue_approx = w_k[0] / x_k_prev[0]
  return x_k, eigenvalue_approx

print(norm_evect_approx1(np.array([1, 9]), 10))

"""**Problem 4**"""

# This function approximates the dominant eigenvalue and eigenvector of an arbitrary matrix using the process described in Problem 4.

def norm_approx_gen(M, x_0, k):
    x_prev = np.array(x_0)
    for i in range(k):
        w = np.dot(M, x_prev)
        max_abs_w = np.max(np.abs(w))
        x_curr = w / max_abs_w
        if i == k - 1:
            x_k_minus_1 = x_prev.copy()
            w_k = w.copy()
        x_prev = x_curr
    lambda_approx = w_k[0] / x_k_minus_1[0]
    return x_prev, lambda_approx

M = np.array([[2, 4, 6], [4, 8, 0], [1, 2, 9]])
x_0 = np.array([1, 5, -1])
iterations = 10
print(norm_approx_gen(M, x_0, iterations))

"""**Problem 5**"""

# This function approximates the dominant eigenvalue and eigenvector of an arbitrary matrix using the Rayleigh quotiend as described in Problem 5.

def ray_quotient(M,x_0,k):
  x_k, _ = norm_approx_gen(M, x_0, k)

  eigenval = np.dot(x_k.T, np.dot(M, x_k)) / np.dot(x_k.T, x_k)

  return eigenval

M = np.array([[2, 4, 6], [4, 8, 0], [1, 2, 9]])
x_0 = np.array([1, 5, -1])
iterations = 10
print(ray_quotient(M, x_0, iterations))

