# This function returns the number of subscribers to the different streaming services after month k.

def subscriber_vals(x_0,k):
  P = np.array([[0.7, 0.2],
                [0.3, 0.8]])
  x_k = x_0
  for _ in range(k):
      # Apply the transition matrix P to the current state x_k
      x_k = np.dot(P, x_k)

  return x_k

# Test case
